import React, { useState } from "react";
import "./Signup.css";
import Modal from "../Modal/Modal";


function Signup(props) {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);

  const onClickHandler = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleSignup = async () => {
    // try {
    //   await firebase.auth().createUserWithEmailAndPassword(email, password);
    //   setIsModalOpen(false);
    //   setEmail("");
    //   setPassword("");
    //   setError(null);
    // } catch (error) {
    //   setError(error.message);
    // }
  };

  return (
    <div className="signup-container">
      <button className="signup-button" onClick={onClickHandler}>
        Sign Up
      </button>
      {isModalOpen && (
        <Modal>
          <div className="modal-content">
            <div className="signup-header">
                <h1 style={{ color: "#20808d" }}>Welcome</h1>
                <p style={{ color: "#18383f" }}>Sign in or sign up to continue</p>
            </div>
            <div className="signup-email-input">
            <input
              type="email"
              placeholder="henry@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            </div>
            <div className="signup-google-button">
            <button
              onClick={handleSignup}
              className="sign-up-button"
              style={{ backgroundColor: "#20808d", borderRadius: "20px" }}
            >
              <span></span><span>Continue with Google</span>
            </button>
            </div>
            <div className="signup-apple-button"></div>
            <button
              onClick={handleSignup}
              className="sign-up-button"
              style={{ backgroundColor: "#20808d", borderRadius: "20px" }}
            >
              <span></span><span>Continue with Apple</span>
            </button>
            {error && <p style={{ color: "red" }}>{error}</p>}
            <button style={{background: "#ffffff", borderRadius: "100%", color: "20808d"}} onClick={closeModal} className="close-button">
              X
            </button>
          </div>
        </Modal>
      )}
    </div>
  );
}

export default Signup;
